mapper.select:
select count(1)
from docs


        and id = #{id}


        and name = #{name}


        and type_id = #{typeId}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and docs_id = #{docsId}


        and user_id = #{userId}

select count(1) == 1
from docs
where
    user_id = #{userId}
    and  docs_id = #{docsId}

select count(1) == 1
from docs
where  docs_id = #{docsId}
