mapper.select:
select count(1)
from notifier


        and id = #{id}


        and user_id = #{userId}


        and notifier_type_id = #{notifierTypeId}


        and name = #{name}


        and config_json = #{configJson}


        and notifier_id = #{notifierId}

select  notify_config
from notifier
where notifier_id = #{notifyId}
